    // テーマ切り替え
    document.addEventListener('DOMContentLoaded', function () {
      const icon = document.querySelector('.js-theme-switch i');
      let currentTheme = localStorage.getItem('theme');
      if (!currentTheme) {
        currentTheme = window.matchMedia('(prefers-color-scheme: light)').matches ? 'light' : 'dark';
      }
      function applyTheme(theme) {
        document.documentElement.setAttribute('data-theme', theme);
        localStorage.setItem('theme', theme);
        icon.classList.remove('fa-sun', 'fa-moon');
        icon.classList.add(theme === 'light' ? 'fa-sun' : 'fa-moon');
      }
      applyTheme(currentTheme);
      document.querySelector('.js-theme-switch').addEventListener('click', function () {
        currentTheme = currentTheme === 'dark' ? 'light' : 'dark';
        applyTheme(currentTheme);
      });
    });
    // はてなブログカード
    document.addEventListener('DOMContentLoaded', function () {
      document.querySelectorAll('.js-post-body a[href]').forEach(function (a) {
        const href = a.getAttribute('href');
        if (!href.startsWith('http')) return;
        const p = a.closest('p');
        if (!p) return;
        const iframe = document.createElement('iframe');
        iframe.className = 'hatenablogcard';
        iframe.style = 'display:block;width:100%;height:auto;margin:12px 0;max-width:680px;';
        iframe.title = href;
        iframe.src = 'https://hatenablog-parts.com/embed?url=' + encodeURIComponent(href);
        iframe.setAttribute('frameborder', '0');
        iframe.setAttribute('scrolling', 'no');
        p.parentNode.insertBefore(iframe, p.nextSibling);
      });
    });
    // 目次作成
    document.addEventListener('DOMContentLoaded', function () {
      const content = document.querySelector('.js-post-body');
      if (!content) return;
      const headings = content.querySelectorAll('h2, h3, h4');
      const tocList = document.createElement('ul');
      tocList.className = 'menu-list';
      const stack = [];
      headings.forEach(function (el, index) {
        const tag = el.tagName.toLowerCase();
        const text = el.textContent.trim();
        const id = 'toc-' + index;
        if (!el.id) el.id = id;
        const level = parseInt(tag.slice(1));
        const li = document.createElement('li');
        const a = document.createElement('a');
        a.href = '#' + id;
        a.textContent = text;
        li.appendChild(a);
        while (stack.length && stack[stack.length - 1].level >= level) {
          stack.pop();
        }
        if (stack.length === 0) {
          tocList.appendChild(li);
          stack.push({ level: level, ul: tocList, li: li });
        } else {
          const parentLi = stack[stack.length - 1].li;
          let subUl = parentLi.querySelector('ul');
          if (!subUl) {
            subUl = document.createElement('ul');
            parentLi.appendChild(subUl);
          }
          subUl.appendChild(li);
          stack.push({ level: level, ul: subUl, li: li });
        }
      });
      const sideToc = document.querySelector('.js-side-toc');
      if (sideToc) sideToc.appendChild(tocList);
    });
    // 目次スクロールハイライト
    document.addEventListener('DOMContentLoaded', function () {
      const tocLinks = document.querySelectorAll('.js-side-toc a');
      const headingSelector = 'h2, h3, h4';
      const tocContainer = document.querySelector('.js-side-toc');
      let lastActiveLink = null;
      window.addEventListener('scroll', function () {
        const currentScroll = window.scrollY;
        let currentId = null;
        document.querySelectorAll(headingSelector).forEach(function (heading) {
          const offsetTop = heading.getBoundingClientRect().top + window.scrollY;
          if (currentScroll >= offsetTop - 225) {
            currentId = heading.id;
          }
        });
        tocLinks.forEach(function (link) {
          link.classList.remove('js-is-active');
          if (currentId && link.getAttribute('href') === '#' + currentId) {
            link.classList.add('js-is-active');
            if (lastActiveLink !== link) {
              lastActiveLink = link;
              link.scrollIntoView({
                behavior: 'smooth',
                block: 'nearest'
              });
            }
          }
        });
      });
    });
