    // 画像モーダル
    document.addEventListener('DOMContentLoaded', () => {
      const postImages = document.querySelectorAll('.js-post-body img');
      const modal = document.querySelector('.js-image-modal');
      const modalImage = modal.querySelector('.js-modal-image');
      const modalClose = modal.querySelector('.js-modal-close');
      const modalBg = modal.querySelector('.js-modal-bg');
      postImages.forEach(img => {
        img.addEventListener('click', () => {
          let src = img.src;
          src = src.replace(/s\d{2,4}(-c)?/, 's1600');
          modalImage.src = src;
          modal.classList.add('is-active');
        });
      });
      [modalClose, modalBg].forEach(el => {
        el.addEventListener('click', () => {
          modal.classList.remove('is-active');
        });
      });
    });
    // サイドカラム表示(モバイル)
    document.addEventListener('DOMContentLoaded', function () {
      var sideButton = document.querySelector('.js-side-button');
      var sideColumn = document.querySelector('.js-side-column');
      var sideBg = document.querySelector('.js-side-bg');
      var sideIcon = sideButton ? sideButton.querySelector('i') : null;
      if (sideButton && sideColumn && sideBg && sideIcon) {
        sideButton.addEventListener('click', function () {
          sideColumn.classList.remove('js-side-out');
          sideColumn.classList.add('js-is-active');
          sideBg.classList.add('js-is-active');
          sideButton.classList.add('js-is-active');
          sideIcon.classList.remove('fa-chevron-left');
          sideIcon.classList.add('fa-chevron-right');
        });
        sideBg.addEventListener('click', function () {
          sideColumn.classList.remove('js-is-active');
          sideColumn.classList.add('js-side-out');
          sideBg.classList.remove('js-is-active');
          sideButton.classList.remove('js-is-active');
          sideIcon.classList.remove('fa-chevron-right');
          sideIcon.classList.add('fa-chevron-left');
          setTimeout(function(){
            sideColumn.classList.remove('js-side-out');
          }, 350);
        });
        var tocLinks = document.querySelectorAll('.js-side-toc a');
        tocLinks.forEach(function(link){
          link.addEventListener('click', function(){
            sideColumn.classList.remove('js-is-active');
            sideColumn.classList.add('js-side-out');
            sideBg.classList.remove('js-is-active');
            sideButton.classList.remove('js-is-active');
            sideIcon.classList.remove('fa-chevron-right');
            sideIcon.classList.add('fa-chevron-left');
            setTimeout(function(){
              sideColumn.classList.remove('js-side-out');
            }, 350);
          });
        });
      }
    });
